(()=>{function s(){let e=document.getElementById("hamburgerbtn"),t=document.getElementById("overlay"),n=document.body;e&&t&&(e.addEventListener("click",function(){t.classList.toggle("open"),e.classList.toggle("is-open"),n.classList.toggle("scroll-lock")}),t.querySelectorAll("a").forEach(o=>{o.addEventListener("click",function(){t.classList.remove("open"),e.classList.remove("is-open"),n.classList.remove("scroll-lock")})}))}document.addEventListener("DOMContentLoaded",function(){s()});})();
